var BG="#222222";var FG="#ffffff";function plot(input,eqn){var layout={showlegend:false,paper_bgcolor:BG,plot_bgcolor:BG,font:{color:FG}};var config={staticPlot:true};var output=input.map(eqn);var trace=[{x:input,y:output,type:"scatter",mode:"markers"}];var TESTER=document.getElementById("tester");Plotly.react(TESTER,trace,layout,config)}function plot_init(){var input=bound_input(0,12,1);var eqn=function(n){return Math.sin(n)};plot(input,eqn)}function bound_input(lower,upper,step){if(lower==""){lower=0}if(upper==""){upper=10}if(step==""){step=1}var list=[];for(var ii=lower;ii<=upper;ii+=step){list.push(ii)}return list}function get_conf(){var in_eqn=document.getElementById("eqn").value;var in_lb=document.getElementById("lower_bound").value;var in_ub=document.getElementById("upper_bound").value;var eqn=expression(in_eqn);var lb=parseFloat(in_lb);var ub=parseFloat(in_ub);assert(lb<ub,"plot: lower bound must be less than upper bound");var input=bound_input(lb,ub,1);plot(input,eqn)}plot_init();
